import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Home() {
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        const fetchPosts = async () => {
            const res = await axios.get('http://localhost:5000/api/posts');
            setPosts(res.data);
        };

        fetchPosts();
    }, []);

    return (
        <div>
            <h1>All Posts</h1>
            {posts.map(post => (
                <div key={post._id}>
                    <h2>{post.title}</h2>
                    <p>{post.body}</p>
                    <p>{post.author}</p>
                </div>
            ))}
        </div>
    );
}

export default Home;
